import logo from './logo.svg';
import './App.css';
import data from './data.json'
import Card from './components/card'
import Checkbox from './components/checkbox'
const OtherComponent = lazy(() => import('./components/suspense'));
const MainComponent = lazy(() => import('./components/box'));
class  App extends React.Component{
  constructor(props){
super(props);
this.state={item:'',filter:new Set()}
  }
  componentDidMount(){
  }
  onChange=(e)=>{
    if(this.state.filter.has(e.target.value)){
this.filter.delete(e.target.value);
this.setState({filter:this.state.filter.delete(e.target.value)})
    }
    else {
      this.setState({filter:this.state.filter.add(e.target.value)})
    }
this.setState({filter:Array.from(this.state.filter)})
  }
  render(){
    const {lang}=this.state;
    let  output=data.filter((x)=>x.language.includes(lang));

    console.log(output)

  return (
    <div className="App">
      <header className="App-header">
     <Checkbox data={data} onChange={this.onChange}/>
      <Suspense fallback={<div>Loading...</div>}>

<Card data={output}/>

      </Suspense>
      </header>
    </div>
  );
  }
}

export default App;
