import React ,{Suspense,lazy}from 'react';
import logo from './logo.svg';
import './App.css';
import data from './data.json'
import Card from './components/card'
import Checkbox from './components/checkbox'
const OtherComponent = lazy(() => import('./components/suspense'));
const MainComponent = lazy(() => import('./components/box'));
class  App extends React.Component{
  constructor(props){
super(props);
this.state={item:'',lang:''}
  }
  onChange=(e)=>{
    if(e.target.value===this.state.lang){
      this.setState({lang:''})
    }
    else{
this.setState({lang:e.target.value})
    }
  }
  render(){
    const {lang}=this.state;
    let  output=data.filter((x)=>x.language.includes(lang));

    console.log(output)

  return (
    <div className="App">
      <header className="App-header">
     <Checkbox data={data} onChange={this.onChange}/>
      <Suspense fallback={<div>Loading...</div>}>

<Card data={output}/>

      </Suspense>
      </header>
    </div>
  );
  }
}

export default App;
